var sizer = window.matchMedia("(min-width: 950px)"),
    mouseTarget = document.getElementById("mouseTarget");
const collection = document.getElementsByClassName("figure.project-index");
let numb = document.getElementsByClassName("figure.project-index").length;
mouseTarget.addEventListener("mouseenter", function() { if (sizer.matches) { document.getElementById("bodyid").style.background = "var(--secondcolor)"; for (let e = 0; e < collection.length; e++) collection[e].style.border = "solid 2px pink" } else { document.getElementById("bodyid").style.background = ""; for (let t = 0; t < collection.length; t++) collection[t].style.border = "solid 2px pink" } }), mouseTarget.addEventListener("mouseleave", function() { if (sizer.matches) { document.getElementById("bodyid").style.background = ""; for (let e = 0; e < collection.length; e++) collection[e].style.border = "solid 2px pink" } else { document.getElementById("bodyid").style.background = ""; for (let t = 0; t < collection.length; t++) collection[t].style.border = "solid 2px pink" } });


function changeColor() {
    const colors = ["red", "blue"]; // Allowed colors
    const randomColor = colors[Math.floor(Math.random() * colors.length)];
    document.documentElement.style.setProperty('--randomcolor', randomColor);
}

// Change color immediately and then every 15 seconds
changeColor();
setInterval(changeColor, 5000);

function changeColor2() {

    const colors2 = ["pink", "yellow"]; // Allowed colors
    const randomColor2 = colors2[Math.floor(Math.random() * colors2.length)];
    document.documentElement.style.setProperty('--randomcolor2', randomColor2);
}

// Change color immediately and then every 15 seconds
changeColor2();
setInterval(changeColor2, 5000);